import ReactDOM from 'react-dom'
import React from 'react'

function tick() {
state = {
    firstName: '',
    showName: false

  }
  let element=(
     <React.Fragment>
       <form>
         <label htmlFor="username">username</label>
         <input
           type="text"
           name="username"
           value={state.username}
           onChange={handleChange}
         />

         <label htmlFor="password">password</label>
         <input
           type="password"
           name="password"
           value={state.password}
           onChange={handleChange}
         />
       </form>

       <h3>Your username is: {state.username}</h3>
     </React.Fragment>
   );
  // highlight-next-line
  ReactDOM.render(element, document.getElementById('root'));
}

class Input extends React.Component {
  state = {
    firstName: '',
    showName: false

  }

  displayNameHandler = (e) => {
    let updatedName = e.target.value;
    this.setState({ firstName: updatedName });
    //console.log(updatedName);
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({
      showName: true
    });
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>Enter the Name</label>
          <input type="text" name="firstName" onChange={this.displayNameHandler} value={this.state.firstName} />
          <button type="submit" onClick={this.handleSubmit}>Submit</button>
          {this.state.showName && <p>"FirstName: " {this.state.firstName}</p>}
        </form>
      </div>
    );
  }
}
