import React from 'react';
import ReactDOM from 'react-dom';

function sqlbuild(inc,ex){
	let query = "SELECT * FROM bookTable WHERE title CONTAINS \'"
	console.log(inc)
	for(let i = 0; i < inc.length-1; i++){
		query+=inc[i] + " And "
	}
	query+=inc[inc.length]
	return query+"\'"
}

//Define component
export default class SearchForm extends React.Component {
  constructor(props) {
    super(props);
//Used to keep track of information
    this.state = {
      state: {
        included: props.included,
        excluded: props.excluded,
        search: "",
        nosearch: "",
        sql:""
      }
    }
  }
//Call when first text box is editted
  handleincludedChanged(event) {
    var state = this.state.state;
    state.included  = event.target.value;

    this.setState({ state: state });
  }
//Call when second text box is editted
  handleexcludedChanged(event) {
    var state = this.state.state;
    state.excluded = event.target.value;

    this.setState({ state: state });
  }
//Call when button clicked
  handleButtonClicked() {
    var state = this.state.state;
    state.search = state.included;
    state.nosearch = state.excluded;
    state.sql = sqlbuild(this.state.state.search.split(" "), this.state.state.nosearch.split(" "))

    this.setState({ state: state });
  }
//Define component layout
  render() {
    return (
      <div>
        <label>
          Search for books including these terms:
        </label>
        <input type="text" value={this.state.state.included} onChange={this.handleincludedChanged.bind(this)}/>
        <br/>
        <label>
          But excluding these terms:
        </label>
        <input type="text" value={this.state.state.excluded} onChange={this.handleexcludedChanged.bind(this)}/>
        <br/>
        <button onClick={this.handleButtonClicked.bind(this)}>
          Search
        </button>
        <br/>
        <label>
			Terms to search for: {this.state.state.search}
		</label>
		<br/>
		<label>
			Terms to exclude: {this.state.state.nosearch}
        </label>
        <br/>
        <br/>
        <label>
			SQL Query: {this.state.state.sql}
        </label>
     </div>

    );
  }
}
//Render component
ReactDOM.render(
  <SearchForm/>,
  document.getElementById('root')
);
