import React from 'react';
import ReactDOM from 'react-dom';

export default class CustomerForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      state: {
        included: props.included,
        excluded: props.excluded,
        search: "",
        nosearch: ""
      }
    }
  }

  handleincludedChanged(event) {
    var state = this.state.state;
    state.included  = event.target.value;

    this.setState({ state: state });
  }

  handleexcludedChanged(event) {
    var state = this.state.state;
    state.excluded = event.target.value;

    this.setState({ state: state });
  }

  handleButtonClicked() {
    var state = this.state.state;
    state.search = state.included;
    state.nosearch = state.excluded;

    this.setState({ state: state });
  }

  render() {
    return (
      <div>
        <label>
          Search for books including these terms:
        </label>
        <input type="text" value={this.state.state.included} onChange={this.handleincludedChanged.bind(this)}/>
        <br/>
        <label>
          But excluding these terms:
        </label>
        <input type="text" value={this.state.state.excluded} onChange={this.handleexcludedChanged.bind(this)}/>
        <br/>
        <button onClick={this.handleButtonClicked.bind(this)}>
          Search
        </button>
        <br/>
        <label>
			Terms to search for: {this.state.state.search}
		</label>
		<br/>
		<label>
			Terms to exclude: {this.state.state.nosearch}
        </label>
     </div>

    );
  }
}

ReactDOM.render(
  // Try changing to isLoggedIn={true}:
  <CustomerForm/>,
  document.getElementById('root')
);
